/* ======================== */
/* 1. Global & Base Styles */
/* ======================== */

body {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    font-family: 'Poppins', sans-serif;
    color: #333;
    margin: 0;

    /* Animated gradient background */
    background: linear-gradient(135deg, #a1c4fd 0%, #c2e9fb 100%);
    background-size: 400% 400%;
    animation: gradient-animation 15s ease infinite;
    overflow-x: hidden;
}

@keyframes gradient-animation {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

/* Custom scrollbar */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 10px;
}

::-webkit-scrollbar-thumb {
    background: #888;
    border-radius: 10px;
    border: 3px solid #f1f1f1;
}

::-webkit-scrollbar-thumb:hover {
    background: #555;
}

/* ======================== */
/* 2. Profile Card & Header */
/* ======================== */

/* Subtle floating effect for the profile card */
@keyframes float {
    0% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-8px);
    }
    100% {
        transform: translateY(0px);
    }
}

.profile-card {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
    border-radius: 20px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);
    padding: 40px;
    width: 90%;
    max-width: 400px;
    text-align: center;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    animation: float 6s ease-in-out infinite;
    position: relative;
    box-sizing: border-box;
}

/* Adjust card width for sub-pages */
body:not(:has(.links-container)) .profile-card {
    max-width: 600px;
}

body:has(#projects-container) .profile-card {
    max-width: 800px;
}

body:has(#bots-container) .profile-card {
    max-width: 700px;
}

.profile-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
    animation-play-state: paused;
}

.avatar {
    width: 150px;
    height: 150px;
    border-radius: 50%;
    object-fit: cover;
    border: 5px solid #fff;
    box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease;
}

.avatar:hover {
    transform: scale(1.05) rotate(5deg);
}

.name {
    margin: 20px 0 5px;
    font-size: 2.5rem;
    font-weight: 600;
    color: #4a4a4a;
}

.bio {
    margin: 0 0 20px;
    color: #666;
    font-size: 1rem;
}

/* ======================== */
/* 3. Links & Back Button  */
/* ======================== */

.links-container {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-top: 30px;
    transition: all 0.5s ease;
}

.link-button {
    text-decoration: none;
    background-color: #4a90e2;
    color: #fff;
    padding: 15px 25px;
    border-radius: 10px;
    font-weight: 600;
    font-size: 1.1rem;
    letter-spacing: 0.5px;
    box-shadow: 0 5px 15px rgba(74, 144, 226, 0.3);
    transition: background-color 0.3s ease, transform 0.2s ease, box-shadow 0.3s ease;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 10px;
}

.link-button:hover {
    background-color: #3b74b8;
    transform: translateY(-3px);
    box-shadow: 0 8px 20px rgba(74, 144, 226, 0.4);
}

/* Space between icon and text */
.link-button i {
    margin-right: 8px;
}

/* Animation for the buttons as they appear */
.hidden-button {
    animation: fadeInSlideUp 0.5s forwards;
}

@keyframes fadeInSlideUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.back-button {
    text-decoration: none;
    background-color: #f0f2f5;
    color: #555;
    padding: 10px 15px;
    border-radius: 8px;
    font-weight: 600;
    font-size: 0.9rem;
    position: absolute;
    top: 20px;
    left: 20px;
    transition: background-color 0.3s ease;
    z-index: 10;
}

.back-button:hover {
    background-color: #e0e2e5;
}

/* ======================== */
/* 4. Blog Post Styles     */
/* ======================== */

.blog-posts-container {
    margin-top: 20px;
    text-align: left;
    width: 100%;
}

.blog-post {
    background-color: #fff;
    border-radius: 15px;
    padding: 25px;
    margin-bottom: 25px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.3s ease;
}

.blog-post:hover {
    transform: translateY(-5px);
}

.post-title {
    font-size: 1.5rem;
    font-weight: 600;
    color: #333;
    margin-top: 0;
}

.post-date {
    font-size: 0.8rem;
    color: #888;
    margin-bottom: 15px;
    border-bottom: 1px solid #ddd;
    padding-bottom: 10px;
}

.post-content p {
    font-size: 1rem;
    line-height: 1.6;
    color: #444;
}

/* ======================== */
/* 5. Project Card Styles  */
/* ======================== */

#projects-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 30px;
    margin-top: 30px;
    width: 100%;
}

.project-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 25px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    text-align: left;
}

.project-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
}

.project-image {
    width: 100%;
    height: 150px;
    object-fit: cover;
    border-radius: 10px;
    margin-bottom: 15px;
}

.project-title {
    font-size: 1.5rem;
    font-weight: 600;
    margin: 0 0 10px;
}

.project-description {
    font-size: 0.9rem;
    color: #666;
    line-height: 1.6;
    margin-bottom: 15px;
}

.project-tech {
    font-size: 0.85rem;
    font-weight: 500;
    color: #4a90e2;
    margin-bottom: 15px;
}

.project-link {
    display: inline-block;
    background-color: #4a90e2;
    color: #fff;
    padding: 10px 15px;
    border-radius: 8px;
    text-decoration: none;
    font-size: 0.9rem;
    transition: background-color 0.3s ease;
}

.project-link:hover {
    background-color: #3b74b8;
}

/* ============================ */
/* 6. Discord Bot Card Styles  */
/* ============================ */

#bots-container {
    display: flex;
    flex-direction: column;
    gap: 30px;
    margin-top: 30px;
    width: 100%;
}

.bot-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 25px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    display: flex;
    align-items: center;
    gap: 20px;
}

.bot-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
}

.bot-avatar {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    object-fit: cover;
    border: 3px solid #7289DA;
    flex-shrink: 0;
}

.bot-details {
    flex-grow: 1;
}

.bot-title {
    font-size: 1.5rem;
    font-weight: 600;
    margin: 0 0 5px;
    color: #333;
}

.bot-description {
    font-size: 0.9rem;
    color: #666;
    line-height: 1.6;
    margin-bottom: 10px;
}

.bot-status {
    font-size: 0.9rem;
    font-weight: 500;
}

.bot-status .online {
    color: #2ecc71;
    font-weight: bold;
}

.bot-status .offline {
    color: #e74c3c;
    font-weight: bold;
}

.bot-links-container {
    display: flex;
    gap: 10px;
    margin-top: 15px;
}

.bot-link {
    display: inline-block;
    color: #fff;
    padding: 10px 15px;
    border-radius: 8px;
    text-decoration: none;
    font-size: 0.9rem;
    font-weight: 500;
    transition: background-color 0.3s ease, border-color 0.3s ease, color 0.3s ease;
    box-shadow: 0 3px 10px rgba(0,0,0,0.1);
}

.bot-link .fa-brands, .bot-link .fa-solid {
    margin-right: 8px;
}

/* Primary "Invite" Button */
.invite-button {
    background-color: #7289DA;
}

.invite-button:hover {
    background-color: #5b6eae;
}

/* Secondary "Website" Button */
.website-button {
    background-color: transparent;
    border: 2px solid #ddd;
    color: #555;
    box-shadow: none;
}

.website-button:hover {
    background-color: #f0f2f5;
    border-color: #c2c2c2;
    color: #333;
}

/* ============================ */
/* 7. API Key Styles      */
/* ============================ */

#api-keys-container {
    display: flex;
    flex-direction: column;
    gap: 20px;
    margin-top: 30px;
    width: 100%;
}

.api-key-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 25px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.08);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.api-key-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
}

.key-title {
    font-size: 1.5rem;
    font-weight: 600;
    margin: 0 0 5px;
    color: #333;
}

.key-description {
    font-size: 0.9rem;
    color: #666;
    line-height: 1.6;
    margin-bottom: 15px;
}

.key-container {
    background-color: #f0f2f5;
    border-radius: 8px;
    padding: 10px 15px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 10px;
    overflow-x: auto;
}

.key-text {
    font-family: 'Courier New', Courier, monospace;
    font-size: 0.9rem;
    color: #555;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.copy-button {
    background-color: #4a90e2;
    color: #fff;
    border: none;
    padding: 8px 12px;
    border-radius: 6px;
    font-size: 0.9rem;
    font-weight: 500;
    cursor: pointer;
    transition: background-color 0.3s ease;
    flex-shrink: 0;
}

.copy-button:hover {
    background-color: #3b74b8;
}

.copy-button:disabled {
    background-color: #2ecc71;
    cursor: not-allowed;
}

.copy-button .fa-solid {
    margin-right: 5px;
}

/* ============================ */
/* 8. Media Queries for Mobile */
/* ============================ */

@media (max-width: 600px) {
    .profile-card {
        padding: 30px 20px;
    }

    .name {
        font-size: 2rem;
    }

    .link-button {
        padding: 12px 20px;
        font-size: 1rem;
    }

    .api-key-card {
        padding: 20px;
    }

    .key-container {
        flex-direction: column;
        align-items: flex-start;
    }

    .copy-button {
        width: 100%;
        margin-top: 10px;
    }
}

document.addEventListener('DOMContentLoaded', function() {
    fetch('buttons.json')
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to load buttons.json');
            }
            return response.json();
        })
        .then(data => {
            const linksContainer = document.querySelector('.links-container');
            const visibleButtonsLimit = 4;
            const buttons = data.buttons || [];

            if (buttons.length > visibleButtonsLimit) {
                // Máº£ng Ä‘á»ƒ lÆ°u trá»¯ cÃ¡c nÃºt áº©n
                const hiddenButtonsArray = [];

                // Hiá»ƒn thá»‹ cÃ¡c nÃºt ban Ä‘áº§u
                buttons.slice(0, visibleButtonsLimit).forEach(buttonData => {
                    linksContainer.appendChild(createButton(buttonData));
                });

                // Táº¡o cÃ¡c nÃºt áº©n vÃ  lÆ°u vÃ o máº£ng
                buttons.slice(visibleButtonsLimit).forEach(buttonData => {
                    const button = createButton(buttonData);
                    button.classList.add('hidden-button');
                    hiddenButtonsArray.push(button);
                });

                // ThÃªm nÃºt Show More vÃ o cuá»‘i danh sÃ¡ch ban Ä‘áº§u
                const showMoreButton = document.createElement('a');
                showMoreButton.href = '#';
                showMoreButton.classList.add('link-button');
                showMoreButton.innerHTML = 'Show More <i class="fa-solid fa-chevron-down"></i>';
                linksContainer.appendChild(showMoreButton);

                // Xá»­ lÃ½ sá»± kiá»‡n khi nháº¥p vÃ o nÃºt Show More
                showMoreButton.addEventListener('click', function(event) {
                    event.preventDefault();

                    // Náº¿u Ä‘ang á»Ÿ tráº¡ng thÃ¡i Show Less (Ä‘Ã£ má»Ÿ rá»™ng)
                    if (linksContainer.classList.contains('expanded')) {
                            // Thu gá»n danh sÃ¡ch
                        linksContainer.classList.remove('expanded');
                        showMoreButton.innerHTML = 'Show Less <i class="fa-solid fa-chevron-down"></i>';
                        // Loáº¡i bá» cÃ¡c nÃºt áº©n ra khá»i DOM
                        const buttonsToRemove = linksContainer.querySelectorAll('.hidden-button');
                        buttonsToRemove.forEach(button => linksContainer.removeChild(button));
                    } else {
                        // Má»Ÿ rá»™ng danh sÃ¡ch
                        linksContainer.classList.add('expanded');
                        showMoreButton.innerHTML = 'Show Less <i class="fa-solid fa-chevron-up"></i>';

                        // ChÃ¨n tá»«ng nÃºt tá»« máº£ng vÃ o trÆ°á»›c nÃºt Show More
                        hiddenButtonsArray.forEach(button => {
                            linksContainer.insertBefore(button, showMoreButton);
                        });

                        // Cuá»™n trang xuá»‘ng nÃºt sau khi má»Ÿ rá»™ng
                        setTimeout(() => {
                            showMoreButton.scrollIntoView({ behavior: 'smooth', block: 'end' });
                        }, 100);
                    }
                });
            } else {
                // Hiá»ƒn thá»‹ táº¥t cáº£ cÃ¡c nÃºt náº¿u sá»‘ lÆ°á»£ng nhá» hÆ¡n giá»›i háº¡n
                buttons.forEach(buttonData => {
                    linksContainer.appendChild(createButton(buttonData));
                });
            }
        })
        .catch(error => {
            console.error('Error fetching or processing data:', error);
            const linksContainer = document.querySelector('.links-container');
            linksContainer.innerHTML = '<p style="color:red;">Could not load buttons. Please check the file.</p>';
        });
});

function createButton(buttonData) {
    const button = document.createElement('a');
    button.href = buttonData.url;
    button.classList.add('link-button');
    button.target = '_blank';
    button.innerHTML = `<i class="${buttonData.icon}"></i> ${buttonData.name}`;
    return button;
}

document.addEventListener('DOMContentLoaded', function() {
    const codeViewerContainer = document.querySelector('.code-viewer-container');

    // CSS styling
    const cssContent = `
.code-viewer-container {
    text-align: left;
    margin-top: 20px;
    width: 100%;
}

.code-block {
    background-color: #2b2b2b;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    margin-bottom: 20px;
    padding: 20px;
    overflow-x: auto;
    transition: transform 0.3s ease;
}

.code-block:hover {
    transform: translateY(-5px);
}

.code-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
    border-bottom: 1px solid #444;
    padding-bottom: 10px;
}

.file-name {
    color: #f0f0f0;
    font-family: 'Poppins', sans-serif;
    font-size: 1.2rem;
    font-weight: 600;
    margin: 0;
}

.copy-button {
    background-color: #569cd6;
    color: #fff;
    border: none;
    border-radius: 6px;
    padding: 8px 12px;
    font-size: 0.9rem;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
}

.copy-button:hover {
    background-color: #4a8ac1;
    transform: translateY(-2px);
}

.copy-button.copied {
    background-color: #2ecc71;
}

pre {
    margin: 0;
    padding: 0;
}

code {
    display: block;
    font-family: 'Courier New', Courier, monospace;
    font-size: 0.9rem;
    line-height: 1.5;
    white-space: pre-wrap;
    word-break: break-all;
    color: #f0f0f0;
}

/* TÃ¹y chá»‰nh mÃ u sáº¯c cho cÃº phÃ¡p highlight (tÆ°Æ¡ng tá»± nhÆ° VS Code) */
.language-html .hljs-tag,
.language-html .hljs-name {
    color: #569cd6;
}
.language-html .hljs-attr,
.language-html .hljs-string {
    color: #9cd67a;
}
.language-css .hljs-selector-tag,
.language-css .hljs-attribute {
    color: #c586c0;
}
.language-css .hljs-property {
    color: #d7ba7d;
}
.language-css .hljs-value {
    color: #9cd67a;
}
.language-js .hljs-keyword {
    color: #c586c0;
}
.language-js .hljs-title,
.language-js .hljs-built_in {
    color: #569cd6;
}
.language-js .hljs-string {
    color: #9cd67a;
}
.language-json .hljs-attr {
    color: #569cd6;
}
.language-json .hljs-string {
    color: #9cd67a;
}
    `;

    // Inject CSS into the document's head
    const styleSheet = document.createElement('style');
    styleSheet.type = 'text/css';
    styleSheet.innerText = cssContent;
    document.head.appendChild(styleSheet);

    // HÃ m Ä‘á»ƒ táº£i vÃ  hiá»ƒn thá»‹ ná»™i dung tá»‡p
    async function loadFileContent() {
        try {
            const response = await fetch('viewsource.json');
            if (!response.ok) {
                throw new Error('Failed to load viewsource.json');
            }
            const data = await response.json();

            for (const file of data.files) {
                // Táº¡o má»™t khá»‘i Ä‘á»ƒ hiá»ƒn thá»‹ mÃ£ nguá»“n
                const codeBlock = document.createElement('div');
                codeBlock.classList.add('code-block');

                // TiÃªu Ä‘á» tá»‡p vÃ  nÃºt copy
                const header = document.createElement('div');
                header.classList.add('code-header');

                const fileName = document.createElement('h3');
                fileName.classList.add('file-name');
                fileName.textContent = file.name;

                const copyButton = document.createElement('button');
                copyButton.classList.add('copy-button');
                copyButton.innerHTML = '<i class="fa-regular fa-copy"></i> Copy Code';

                // ThÃªm sá»± kiá»‡n copy vÃ o nÃºt
                copyButton.addEventListener('click', async () => {
                    try {
                        await navigator.clipboard.writeText(file.content);
                        copyButton.innerHTML = '<i class="fa-solid fa-check"></i> Copied!';
                        copyButton.classList.add('copied');
                        setTimeout(() => {
                            copyButton.innerHTML = '<i class="fa-regular fa-copy"></i> Copy Code';
                            copyButton.classList.remove('copied');
                        }, 2000);
                    } catch (err) {
                        console.error('Failed to copy text: ', err);
                        copyButton.innerHTML = '<i class="fa-solid fa-xmark"></i> Failed!';
                    }
                });

                header.appendChild(fileName);
                header.appendChild(copyButton);

                // Pháº§n hiá»ƒn thá»‹ code
                const preElement = document.createElement('pre');
                const codeElement = document.createElement('code');

                // ThÃªm class cho cÃº phÃ¡p highlight
                if (file.name.endsWith('.html')) {
                    codeElement.classList.add('language-html');
                } else if (file.name.endsWith('.css')) {
                    codeElement.classList.add('language-css');
                } else if (file.name.endsWith('.js')) {
                    codeElement.classList.add('language-js');
                } else if (file.name.endsWith('.json')) {
                    codeElement.classList.add('language-json');
                }

                // Chuyá»ƒn Ä‘á»•i cÃ¡c kÃ½ tá»± Ä‘áº·c biá»‡t Ä‘á»ƒ hiá»ƒn thá»‹ Ä‘Ãºng trong HTML
                codeElement.textContent = file.content;

                preElement.appendChild(codeElement);
                codeBlock.appendChild(header);
                codeBlock.appendChild(preElement);
                codeViewerContainer.appendChild(codeBlock);
            }
        } catch (error) {
            console.error('Error loading files:', error);
            codeViewerContainer.innerHTML = '<p style="color:red; text-align: center;">Could not load source files. Please check file paths.</p>';
        }
    }

    loadFileContent();
});

document.addEventListener('DOMContentLoaded', function() {
    fetch('apikey.json')
        .then(response => {
            if (!response.ok) {
                throw new Error('Failed to load apikey.json');
            }
            return response.json();
           })
           .then(data => {
               const container = document.getElementById('api-keys-container');

               if (data.apikeys && Array.isArray(data.apikeys)) {
                   data.apikeys.forEach(apiKeyData => {
                       const keyCard = document.createElement('div');
                       keyCard.classList.add('api-key-card');

                       const title = document.createElement('h3');
                       title.classList.add('key-title');
                       title.textContent = apiKeyData.name;

                       const description = document.createElement('p');
                       description.classList.add('key-description');
                       description.textContent = apiKeyData.description;

                       const keyContainer = document.createElement('div');
                       keyContainer.classList.add('key-container');

                       const keyText = document.createElement('span');
                       keyText.classList.add('key-text');
                       keyText.textContent = apiKeyData.key;
                       keyContainer.appendChild(keyText);

                       const copyButton = document.createElement('button');
                       copyButton.classList.add('copy-button');
                       copyButton.innerHTML = `<i class="fa-solid fa-copy"></i> Copy Key`;

                       // Add copy functionality to the button
                       copyButton.addEventListener('click', () => {
                           navigator.clipboard.writeText(apiKeyData.key).then(() => {
                               copyButton.textContent = 'Copied!';
                               copyButton.disabled = true;
                               setTimeout(() => {
                                   copyButton.innerHTML = `<i class="fa-solid fa-copy"></i> Copy Key`;
                                   copyButton.disabled = false;
                               }, 2000);
                           }).catch(err => {
                               console.error('Could not copy text: ', err);
                               alert('Failed to copy key. Your browser might not support this feature on a local file.');
                           });
                       });

                       keyContainer.appendChild(copyButton);

                       keyCard.appendChild(title);
                       keyCard.appendChild(description);
                       keyCard.appendChild(keyContainer);

                       container.appendChild(keyCard);
                   });
               } else {
                   container.innerHTML = '<p>No API keys found.</p>';
               }
           })
           .catch(error => {
               console.error('Error fetching or rendering API keys:', error);
